"""
BF6 Rank Tracker — Redesigned
Requirements: pip install PySide6 requests
"""
import sys, base64, requests
from PySide6.QtWidgets import (
    QApplication, QWidget, QVBoxLayout, QHBoxLayout,
    QLabel, QLineEdit, QPushButton
)
from PySide6.QtCore  import Qt, QTimer, QThreadPool, QRunnable, QObject, Signal, Slot, QByteArray
from PySide6.QtGui   import (
    QFont, QFontDatabase, QPixmap, QPainter, QColor, QPalette,
    QRadialGradient, QBrush, QFontMetrics, QPen
)

# ── Palette ───────────────────────────────────────────────────────────────────
BG      = "#0a0908"
SURFACE = "#111009"
EDGE    = "#1e1c17"
GOLD    = "#c9a84c"
GOLD2   = "#e8c96a"
GOLD_DIM= "#3a2f10"
TEXT    = "#e8e0d4"
MUTED   = "#4e4840"
GREEN   = "#4fc87a"
RED     = "#e05252"
API_URL = "https://api.gametools.network/bf6/profile/"

_FONT_B64 = ""

def load_font():
    data = QByteArray(base64.b64decode(_FONT_B64))
    fid  = QFontDatabase.addApplicationFontFromData(data)
    fams = QFontDatabase.applicationFontFamilies(fid)
    return fams[0] if fams else None


# ── Workers ───────────────────────────────────────────────────────────────────
class Signals(QObject):
    result = Signal(dict)
    error  = Signal(str)
    image  = Signal(object)   # raw bytes

class FetchWorker(QRunnable):
    def __init__(self, name):
        super().__init__()
        self.name    = name
        self.signals = Signals()

    @Slot()
    def run(self):
        try:
            r = requests.get(
                API_URL,
                params={"name": self.name, "platform": "pc", "skip_battlelog": "true"},
                timeout=10
            )
            r.raise_for_status()
            profiles = r.json().get("playerProfiles", [])
            if not profiles:
                self.signals.error.emit("Player not found.")
                return
            card = profiles[0].get("playerCard", {})
            self.signals.result.emit({
                "name":     self.name,
                "rank":     card.get("rank", 0),
                "rank_img": card.get("rankImage", {}).get("small", "")
            })
        except requests.exceptions.ConnectionError:
            self.signals.error.emit("No connection.")
        except requests.exceptions.Timeout:
            self.signals.error.emit("Timed out.")
        except Exception as e:
            self.signals.error.emit(str(e))

class ImageWorker(QRunnable):
    def __init__(self, url):
        super().__init__()
        self.url     = url
        self.signals = Signals()

    @Slot()
    def run(self):
        try:
            r = requests.get(self.url, timeout=8)
            r.raise_for_status()
            self.signals.image.emit(r.content)   # raw bytes
        except Exception:
            self.signals.image.emit(b"")


# ── Badge ─────────────────────────────────────────────────────────────────────
class BadgeWidget(QWidget):
    BADGE = 160
    PAD   = 28
    TOTAL = BADGE + PAD * 2   # 216

    def __init__(self, ff, parent=None):
        super().__init__(parent)
        self.ff    = ff
        self._pix  = None
        self._rank = ""
        self.setFixedSize(self.TOTAL, self.TOTAL)

    def set_data(self, raw, rank):
        """raw: bytes from ImageWorker, or QPixmap for fallback empty case."""
        self._rank = str(rank) if rank else ""
        if isinstance(raw, (bytes, bytearray)) and raw:
            pix = QPixmap()
            pix.loadFromData(raw)
            self._pix = pix.scaled(self.BADGE, self.BADGE, Qt.KeepAspectRatio,
                                   Qt.SmoothTransformation) if not pix.isNull() else None
        else:
            self._pix = None
        self.update()

    def clear(self):
        self._pix  = None
        self._rank = ""
        self.update()

    def paintEvent(self, _):
        p = QPainter(self)
        p.setRenderHints(QPainter.Antialiasing | QPainter.SmoothPixmapTransform)
        cx = cy = self.TOTAL // 2

        if self._pix:
            # Gold radial glow
            glow = QRadialGradient(cx, cy, self.BADGE * 0.72)
            glow.setColorAt(0.0, QColor(201, 168, 76, 50))
            glow.setColorAt(0.55, QColor(201, 168, 76, 14))
            glow.setColorAt(1.0, QColor(0, 0, 0, 0))
            p.fillRect(self.rect(), QBrush(glow))

            bx = cx - self._pix.width()  // 2
            by = cy - self._pix.height() // 2
            p.drawPixmap(bx, by, self._pix)

            # Rank overlay — lower-third of badge, large and centred
            if self._rank:
                from PySide6.QtCore import QRect
                if self.ff:
                    rf = QFont(self.ff, 26, QFont.Bold)
                else:
                    rf = QFont("Arial", 26, QFont.Bold)
                p.setFont(rf)
                bw = self._pix.width()
                bh = self._pix.height()
                # Zone: bottom 40% of badge image
                zone_h = int(bh * 0.40)
                zone_y = by + bh - zone_h - int(bh * 0.06)
                overlay_rect = QRect(bx, zone_y, bw, zone_h)
                # Dark semi-transparent shadow (offset 2px)
                shadow_rect = QRect(bx + 2, zone_y + 2, bw, zone_h)
                p.setPen(QColor(0, 0, 0, 210))
                p.drawText(shadow_rect, Qt.AlignHCenter | Qt.AlignVCenter, self._rank)
                # Bright gold
                p.setPen(QColor(GOLD2))
                p.drawText(overlay_rect, Qt.AlignHCenter | Qt.AlignVCenter, self._rank)

        else:
            # Placeholder dashed ring
            pen = QPen(QColor(EDGE))
            pen.setWidth(1)
            pen.setStyle(Qt.DashLine)
            p.setPen(pen)
            p.setBrush(Qt.NoBrush)
            r = self.BADGE // 2 - 4
            p.drawEllipse(cx - r, cy - r, r * 2, r * 2)

        p.end()


# ── Pulse Dot ─────────────────────────────────────────────────────────────────
class PulseDot(QWidget):
    def __init__(self, parent=None):
        super().__init__(parent)
        self.setFixedSize(7, 7)
        self._on    = False
        self._color = QColor(GREEN)
        self._timer = QTimer(self)
        self._timer.timeout.connect(self._blink)

    def start(self, color=GREEN):
        self._color = QColor(color)
        self._on    = True
        self._timer.start(650)
        self.update()

    def stop(self):
        self._timer.stop()
        self._on = False
        self.update()

    def _blink(self):
        self._on = not self._on
        self.update()

    def paintEvent(self, _):
        p = QPainter(self)
        p.setRenderHint(QPainter.Antialiasing)
        p.setBrush(self._color if self._on else QColor(MUTED))
        p.setPen(Qt.NoPen)
        p.drawEllipse(0, 0, 7, 7)
        p.end()


# ── Main Window ───────────────────────────────────────────────────────────────
class MainWindow(QWidget):
    REFRESH = 60

    def __init__(self):
        super().__init__()
        self.setWindowTitle("BF6 Rank Tracker")
        self.setAttribute(Qt.WA_StyledBackground, True)
        self.setStyleSheet(f"background: {BG};")

        self._ff       = load_font()
        self._pool     = QThreadPool.globalInstance()
        self._player   = ""
        self._countdown = 0

        self._build_ui()

        self._refresh_timer   = QTimer(self)
        self._refresh_timer.timeout.connect(self._auto_refresh)
        self._countdown_timer = QTimer(self)
        self._countdown_timer.setInterval(1000)
        self._countdown_timer.timeout.connect(self._tick_countdown)

        self._workers = []   # keep ImageWorker refs alive until signal fires
        self.setFixedWidth(360)

    # ── build ─────────────────────────────────────────────────────────────────
    def _build_ui(self):
        ff = self._ff

        root = QVBoxLayout(self)
        root.setContentsMargins(28, 28, 28, 22)
        root.setSpacing(0)

        # ── Search row ────────────────────────────────────────────────────────
        search_row = QHBoxLayout()
        search_row.setSpacing(0)
        search_row.setContentsMargins(0, 0, 0, 0)

        self._field = QLineEdit()
        self._field.setPlaceholderText("Player name…")
        self._field.setFixedHeight(38)
        if ff:
            self._field.setFont(QFont(ff, 10))
        self._field.returnPressed.connect(self._submit)
        self._field.setStyleSheet(f"""
            QLineEdit {{
                background: {SURFACE};
                border: 1px solid {EDGE};
                border-right: none;
                border-top-left-radius: 5px;
                border-bottom-left-radius: 5px;
                color: {TEXT};
                padding: 0 12px;
            }}
            QLineEdit:focus {{
                border-color: #2e2a1c;
                outline: none;
            }}
        """)

        self._btn = QPushButton("TRACK")
        self._btn.setFixedSize(68, 38)
        self._btn.setCursor(Qt.PointingHandCursor)
        self._btn.clicked.connect(self._submit)
        if ff:
            bf = QFont(ff, 8)
            bf.setLetterSpacing(QFont.AbsoluteSpacing, 1.8)
            self._btn.setFont(bf)
        self._btn.setStyleSheet(f"""
            QPushButton {{
                background: {GOLD_DIM};
                border: 1px solid #4a3b14;
                border-top-right-radius: 5px;
                border-bottom-right-radius: 5px;
                color: {GOLD2};
            }}
            QPushButton:hover  {{ background: #4a3b14; }}
            QPushButton:pressed {{ background: {GOLD_DIM}; }}
            QPushButton:disabled {{ color: {MUTED}; background: {SURFACE}; border-color: {EDGE}; }}
        """)

        search_row.addWidget(self._field)
        search_row.addWidget(self._btn)
        root.addLayout(search_row)

        # ── Badge (hero) ──────────────────────────────────────────────────────
        root.addSpacing(26)
        badge_row = QHBoxLayout()
        badge_row.setContentsMargins(0, 0, 0, 0)
        self._badge = BadgeWidget(ff, self)
        badge_row.addStretch(1)
        badge_row.addWidget(self._badge)
        badge_row.addStretch(1)
        root.addLayout(badge_row)

        # ── Name ─────────────────────────────────────────────────────────────
        root.addSpacing(8)
        self._name_lbl = QLabel("")
        self._name_lbl.setAlignment(Qt.AlignCenter)
        if ff:
            nf = QFont(ff, 28, QFont.Bold)
        else:
            nf = QFont("Arial", 18, QFont.Bold)
        self._name_lbl.setFont(nf)
        self._name_lbl.setStyleSheet(f"color: {TEXT};")
        root.addWidget(self._name_lbl)

        # ── Sub-line (rank or message) ────────────────────────────────────────
        root.addSpacing(3)
        self._sub = QLabel("search for a player to begin")
        self._sub.setAlignment(Qt.AlignCenter)
        if ff:
            sf = QFont(ff, 9)
        else:
            sf = QFont("Arial", 9)
        self._sub.setFont(sf)
        self._sub.setStyleSheet(f"color: {MUTED}; letter-spacing: 0.5px;")
        root.addWidget(self._sub)

        # ── Status bar ────────────────────────────────────────────────────────
        root.addSpacing(22)
        bar = QHBoxLayout()
        bar.setContentsMargins(0, 0, 0, 0)
        bar.setSpacing(5)

        self._dot = PulseDot(self)
        self._status = QLabel("IDLE")
        if ff:
            stf = QFont(ff, 7)
            stf.setLetterSpacing(QFont.AbsoluteSpacing, 1.2)
            self._status.setFont(stf)
        self._status.setStyleSheet(f"color: {MUTED};")

        self._refresh_lbl = QLabel("")
        if ff:
            self._refresh_lbl.setFont(stf)
        self._refresh_lbl.setStyleSheet(f"color: {MUTED};")

        # align dot vertically with label
        dot_wrap = QWidget()
        dot_lay  = QHBoxLayout(dot_wrap)
        dot_lay.setContentsMargins(0, 2, 0, 0)
        dot_lay.addWidget(self._dot)

        bar.addWidget(dot_wrap)
        bar.addWidget(self._status)
        bar.addStretch()
        bar.addWidget(self._refresh_lbl)
        root.addLayout(bar)

    # ── logic ─────────────────────────────────────────────────────────────────
    def _submit(self):
        name = self._field.text().strip()
        if not name:
            return
        self._player = name
        self._set_busy(True)
        self._badge.clear()
        self._name_lbl.setText("")
        self._sub.setStyleSheet(f"color: {MUTED}; letter-spacing: 0.5px;")
        self._sub.setText("fetching…")
        self._dot.stop()
        self._status.setText("FETCHING")
        self._refresh_timer.stop()
        self._countdown_timer.stop()
        self._refresh_lbl.setText("")
        self._do_fetch(name)

    def _do_fetch(self, name):
        w = FetchWorker(name)
        w.signals.result.connect(self._on_result)
        w.signals.error.connect(self._on_error)
        self._pool.start(w)

    def _on_result(self, data):
        rank    = data.get("rank", 0)
        img_url = data.get("rank_img", "")
        name    = data.get("name", self._player)

        self._name_lbl.setText(name)
        self._sub.setText("")   # rank lives on the badge now

        # Fetch image — keep ref so Python doesn't GC signals before they fire
        if img_url:
            iw = ImageWorker(img_url)
            self._workers.append(iw)
            def _on_img(raw, _iw=iw, _rank=rank):
                self._badge.set_data(raw, _rank)
                if _iw in self._workers:
                    self._workers.remove(_iw)
            iw.signals.image.connect(_on_img)
            self._pool.start(iw)
        else:
            self._badge.set_data(b"", rank)

        self._set_busy(False)
        self._status.setText("LIVE")
        self._dot.start(GREEN)
        self._start_countdown()
        self._resize_to(name)

    def _on_error(self, msg):
        self._sub.setText(msg)
        self._sub.setStyleSheet(f"color: {RED};")
        self._status.setText("ERROR")
        self._dot.stop()
        self._set_busy(False)
        self._refresh_timer.stop()
        self._countdown_timer.stop()
        self._refresh_lbl.setText("")

    def _start_countdown(self):
        self._countdown = self.REFRESH
        self._refresh_lbl.setText(f"↻  {self._countdown}s")
        self._countdown -= 1
        self._countdown_timer.start()
        self._refresh_timer.start(self.REFRESH * 1000)

    def _auto_refresh(self):
        self._status.setText("FETCHING")
        self._do_fetch(self._player)

    def _tick_countdown(self):
        if self._countdown > 0:
            self._refresh_lbl.setText(f"↻  {self._countdown}s")
            self._countdown -= 1
        else:
            self._refresh_lbl.setText("")
            self._countdown_timer.stop()

    def _set_busy(self, busy):
        self._field.setEnabled(not busy)
        self._btn.setEnabled(not busy)

    def _resize_to(self, name):
        if self._ff:
            nf = QFont(self._ff, 20, QFont.Bold)
        else:
            nf = QFont("Arial", 18, QFont.Bold)
        fm   = QFontMetrics(nf)
        nw   = fm.horizontalAdvance(name) + 80
        bw   = self._badge.TOTAL + 56
        want = max(nw, bw, 300)
        if want > self.width():
            self.setFixedWidth(want)


def main():
    app = QApplication(sys.argv)
    app.setApplicationName("BF6 Rank Tracker")

    pal = app.palette()
    pal.setColor(QPalette.Window,          QColor(BG))
    pal.setColor(QPalette.WindowText,      QColor(TEXT))
    pal.setColor(QPalette.Base,            QColor(SURFACE))
    pal.setColor(QPalette.Text,            QColor(TEXT))
    pal.setColor(QPalette.PlaceholderText, QColor(MUTED))
    app.setPalette(pal)

    w = MainWindow()
    w.show()
    sys.exit(app.exec())


if __name__ == "__main__":
    main()
